'use client'
import { VERSION, API_VERSION } from '../../../../../../version.js';
import { isGlobalWalletPopup } from '../../../../../shared/utils/functions/isGlobalWalletPopup/isGlobalWalletPopup.js';
import '@dynamic-labs/sdk-api-core';
