import 'react-international-phone';
import { getDeviceFingerprint } from '../../../../../utils/functions/getDeviceFingerprint/getDeviceFingerprint.js';
import '@dynamic-labs/iconic';
import '@dynamic-labs/wallet-connector-core';
import 'react';
