'use client'
import { onEvent } from '@dynamic-labs-sdk/client';
import { dynamicEvents } from '../../../../../events/dynamicEvents.js';

const syncEvents = (client) => {
    onEvent({
        event: 'mfaCompletionSuccess',
        listener: ({ mfaToken }) => {
            dynamicEvents.emit('mfaCompletionSuccess', { mfaToken });
        },
    }, client);
    onEvent({
        event: 'mfaCompletionFailure',
        listener: ({ error }) => {
            dynamicEvents.emit('mfaCompletionFailure', { error });
        },
    }, client);
    onEvent({
        event: 'logout',
        listener: () => {
            dynamicEvents.emit('logout');
        },
    }, client);
};

export { syncEvents };
