const { getConnectedWalletsInfo, resetConnectedWalletsInfo: resetConnectedWalletsInfoStore, setConnectedWalletsInfo: setConnectedWalletsInfoStore, useConnectedWalletsInfo, } = createStoreState('connectedWalletsInfo');
// We need to sync the unverified wallet accounts with the connected wallets info
// for the JS SDK to know when there's an active "session" and not do things like refetch project settings, etc
const resetConnectedWalletsInfo = () => {
    setUnverifiedWalletAccounts([]);
    resetConnectedWalletsInfoStore();
};
const setConnectedWalletsInfo = (value) => {
    setUnverifiedWalletAccounts(value);
    setConnectedWalletsInfoStore(value);
};

export { getConnectedWalletsInfo, resetConnectedWalletsInfo, setConnectedWalletsInfo, useConnectedWalletsInfo };
