/**
 * Opens a view that lets the user choose between funding methods.
 *
 * Must be logged in to use.
 */
const useOpenFundingOptions = () => {
    const { setDynamicWidgetView } = DynamicWidgetContext.useWidgetContext();
    const { setShowDynamicUserProfile } = useInternalDynamicContext.useInternalDynamicContext();
    const openFundingOptions = React.useCallback(() => {
        setDynamicWidgetView('choose-wallet-funding-method');
        setShowDynamicUserProfile(true);
    }, [setDynamicWidgetView, setShowDynamicUserProfile]);
    return React.useMemo(() => ({
        openFundingOptions,
    }), [openFundingOptions]);
};

exports.useOpenFundingOptions = useOpenFundingOptions;
