'use client'
import { useCallback } from 'react';
import { signInWithPasskey } from '@dynamic-labs-sdk/client';

/**
 * Sign in with passkey
 *
 * @returns Function to sign in with a passkey, returns a VerifyResponse
 *
 * @example
 * ```tsx
 * const App = () => {
 *   const signInWithPasskey = useSignInWithPasskey();
 *
 *   return (
 *     <button
 *       onClick={() => signInWithPasskey()}
 *     >
 *       Sign in with passkey
 *     </button>
 *  );
 * }
 */
const useSignInWithPasskey = ({ relatedOriginRpId, } = {}) => useCallback(() => signInWithPasskey({ relatedOriginRpId }), [relatedOriginRpId]);

export { useSignInWithPasskey };
